/** setCookie 함수 파라미터 */
export interface SetCookieParamter {
    /** cookie name */
    name: string;
    /** cookie value */
    value: string;
    /** cookie에 접근 가능한 경로 */
    path?: string;
    /** cookie에 접근 가능한 도메인 */
    domain?: string;
    /** cookie 생성하는 시점 기준으로 몇 일 뒤까지 cookie 유효 일자를 설정할지 결정 */
    expires?: string;
    /** HTTPS로 통신하는 경우에만 쿠키를 전송할 건지 결정 */
    secure?: 'secure';
}
export type SetCookieOptionParamter = Pick<SetCookieParamter, 'path' | 'domain' | 'expires' | 'secure'>;
